export class SIZE{
    /*対象の要素が画面の縦向きに真ん中になるようにする*/
    static SetElementMiddle(target){
        if(!document.getElementById(target)){
            return;
        }
        const tobj = document.getElementById(target);
        tobj.setAttribute('style', `top:${(window.innerHeight - tobj.offsetHeight) / 2}px;`);
    }
}
